mapper.sql:

RESERVATION_DATE >= #{dateTime}
AND
RESERVATION_DATE < DATE_ADD(DATE(#{dateTime}), INTERVAL 1 DAY)

mapper.update:
UPDATE
    TB_RESERVATION
SET
    RESERVATION_DATE = #{dateTime}
WHERE
    UUID = #{reservationId}

UPDATE
    TB_RESERVATION
SET
    STATUS = #{status}
WHERE
    UUID = #{uuid}
