mapper.select:
  	select t.*
	from tb_treatment t
	join tb_reception r on t.uuid = r.uuid
	where  r.doctor_uuid = #{doctorUuid} and r.patient_uuid = #{patientUuid}
	order by t.treat_date desc
	limit #{start}, #{size}

	  SELECT COUNT(*)
	  FROM TB_TREATMENT T 
	  JOIN TB_RECEPTION R ON T.uuid = R.uuid
	  WHERE R.doctor_uuid = #{doctorUuid}
    	AND R.patient_uuid = #{patientUuid}

select *
from tb_treatment t
where t.id =#{id}
