mapper.select:
SELECT t.*
FROM TB_TREATMENT t
JOIN TB_RECEPTION r ON t.uuid = r.uuid
WHERE r.doctor_uuid = #{doctorUuid}
  AND r.patient_uuid = #{patientUuid}
ORDER BY t.treat_date DESC
LIMIT #{start}, #{size}

SELECT COUNT(*)
FROM TB_TREATMENT t
JOIN TB_RECEPTION r ON t.uuid = r.uuid
WHERE r.doctor_uuid = #{doctorUuid}
  AND r.patient_uuid = #{patientUuid}

SELECT *
FROM TB_TREATMENT t
WHERE t.id = #{id}
